mapper.select:
SELECT COUNT(DISTINCT p.product_id)
FROM product p
JOIN product_opt po ON p.product_id = po.product_id




        p.category_id = #{categoryId}



        AND po.size IN

            #{size}




        AND po.color IN

            #{color}




        AND p.price BETWEEN #{minPrice} AND #{maxPrice}

SELECT p.product_id AS productId, p.title AS title, p.content AS content, p.code AS code, p.contry AS contry, p.material AS material, p.features AS features, p.price AS price, p.manufacture_date AS manufactureDate, p.category_id AS categoryId,
po.color AS color, po.size AS size, po.quantity AS quantity
FROM product p LEFT JOIN product_opt po ON p.product_id = po.product_id
p.category_id = #{categoryId}
AND po.size IN
#{size}
AND po.color IN
#{color}
AND p.price BETWEEN #{minPrice} AND #{maxPrice}
ORDER BY p.product_id LIMIT #{limit} OFFSET #{offset}
